import UIKit
import Foundation

class FirstScreenRouter: BaseRouter {
    
    // MARK: - Private properties
    private let storyboard = UIStoryboard(name: "FirstScreen", bundle: nil)
    
    // MARK: Module Setup
    init() {
        let moduleViewController = storyboard.instantiateViewController(ofType: FirstScreenViewController.self)
        super.init(viewController: moduleViewController)
        let interactor = FirstScreenInteractor()
        let presenter = FirstScreenPresenter(view: moduleViewController, interactor: interactor, router: self)
        moduleViewController.presenter = presenter
        interactor.presenter = presenter
    }
    
}

